select person.ethnicity,person.gender,count(*) from client,person where client.person_id = person.id group by person.ethnicity,person.gender union (select person.gender,person.ethnicity,count(*) from client,person where client.person_id = person.id group by person.gender,person.ethnicity) union (select person.ethnicity,person.gender,count(*) from client,person where client.person_id = person.id group by person.ethnicity,person.gender);