select organization.name,organization.type,organization.website,person.first_name,person.last_name from org_donations,organization,person where person.id = organization.person_id and  organization.name = org_donations.org_name and org_donations.anonymous='N' and not exists (select organization.name from organization,donation_drive_sponsor where organization.name = donation_drive_sponsor.name) order by organization.name;
